func TestEthProve(t *testing.T) {
	var accountProof = []string{"", "0xf87180a0314d7dba2b562fb3777e4efa09ce1a92d4b2d9cf01b8d413f2d4f047984e6cec808080a04ed389f42aa6995eca849e6fcf96a32ced81fe1e1bd2f1d2cdb03c34154a7a22808080808080a025f8c45eb48ad1d050bbd03e269ad41869e85de4a533cb515b5a2f436db320d980808080", "0xf873a0201653ff4a6d0660d11912ce77346791b671daab4947be800a7da66978666d54b850f84e378a01233cdb0fbbf668c0b3a056e81f171bcc55a6ff8345e692c0f86e5b48e01b996cadc001622fb5e363b421a0c5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470"}

	var key = "0ac45f1e6b8d47ac4c73aee62c52794b5898da9f"

	var w, err = hex.DecodeString(key)
	if err != nil {
		return
	}

	key = hex.EncodeToString(crypto.Keccak256(w))

	fmt.Println(key)

	fmt.Println(VerifyProof("0xea165aeb2a28fbaef6c538444f9cc33f7190f5205273451ad348ca0162dc863e", key, "0xf84e378a01233cdb0fbbf668c0b3a056e81f171bcc55a6ff8345e692c0f86e5b48e01b996cadc001622fb5e363b421a0c5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470", accountProof))
}
